void oled_task_user(void) {
    if (is_keyboard_master()) {
        switch (get_highest_layer(layer_state)) {
            case _QWERTY:
                render_qwerty_left();
                break;
            case _LSYMB:
            case _RSYMB:
                render_symbols_left();
                break;
            case _LNUMB:
            case _RNUMB:
                render_numeric_left();
                break;
            case _LNAVI:
            case _RNAVI:
                render_navigation_left();
                break;
            case _SVORAK:
            default:
                render_svorak_left();
        }
    } else {
        switch (get_highest_layer(layer_state)) {
            case _QWERTY:
                render_qwerty_right();
                break;
            case _LSYMB:
            case _RSYMB:
                render_symbols_right();
                break;
            case _LNUMB:
            case _RNUMB:
                render_numeric_right();
                break;
            case _LNAVI:
            case _RNAVI:
                render_navigation_right();
                break;
            case _SVORAK:
            default:
                render_svorak_right();
        }
    }
}

